const botName = "NazBorg";

// twitch bot settings
const config = {
  connection: {
    cluster: "aws",
    reconnect: true,
  },
  identity: {
    username: botName,
    password: "oauth:jpp29hh7etadbvznq4l0ra5xhwf48i",
  },
  channels: channels,
};

if (channels.length) {
  const client = new tmi.client(config);
  client.connect();

  client.on("chat", (channel, user, message, self) => {
    if (self) return;

    if (message.startsWith("!")) {
      // destructuring the message to get the command and three different parameters in separated variables
      const [command] = message.split(" ").filter((i) => i !== "");
      switch (command) {
        case `!${commandName}`:
          console.log(user);
          const userColor = user.color;
          commandShoot(userColor);
          break;
      }
    }
  });
}

